mapper.select:
SELECT * FROM chat_message cm INNER JOIN chat_room rm ON cm.room_id = rm.room_id WHERE cm.room_id IN
#{roomId}
AND cm.TIMESTAMP = ( SELECT MAX( TIMESTAMP ) FROM chat_message WHERE room_id = cm.room_id )

mapper.update:
UPDATE chat_message
SET status = #{newStatus}
WHERE message_id IN

    #{id}
